import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import Cookies from "universal-cookie";
import { verifyToken, getTodos } from "../APIFunctions";

export default function ViewTodos() {
  const cookies = new Cookies();
  const history = useHistory();
  const validation = async () => {
    if (cookies.get("token") == undefined) {
      history.push("/login");
    }

    if ((await verifyToken(cookies.get("token"))) == false) {
      cookies.remove("token", { path: "/" });
      history.push("/login");
      window.location.reload();
    }
  };

  useEffect(async () => {
    validation();
    let todos = await getTodos(cookies.get("token"));
  }, []);

  return (
    <div class="container">
      <h1>Hello There,</h1>
      <div class="card">
        <div class="card-header">Your Todos:</div>
        <ul class="list-group list-group-flush">
          <li class="list-group-item">Cras justo odio</li>
          <li class="list-group-item">Dapibus ac facilisis in</li>
          <li class="list-group-item">Vestibulum at eros</li>
        </ul>
      </div>
    </div>
  );
}
